var hide_lists=function(cb){$('.my-name').show();$('.content').fadeOut(300);$('.waves-effect').removeClass('disabled')};
var show_projects=function(){$('.my-name').show();$('.waves-effect').removeClass('disabled');$('.content').hide();$('#projects').show();$('#projects-btn').addClass('disabled')};
var show_posts=function(){$('.my-name').show();$('.waves-effect').removeClass('disabled');$('.content').hide();$('#posts').show();$('#posts-btn').addClass('disabled')};
var show_resume=function(){$('.my-name').hide();$('.waves-effect').removeClass('disabled');$('.content').hide();$('#resume').show();$('#resume_btn').addClass('disabled')};